mapper.select:

SELECT * FROM (
    SELECT ROWNUM rn, a.*
    FROM (
        SELECT q.*, an.answer_idx FROM tb_qna q left join tb_answer an on q.qna_idx = an.qna_idx ORDER BY q.created_at DESC
    ) a
    WHERE ROWNUM <= #{endRow}
)
WHERE rn >= #{startRow}

SELECT * FROM ( SELECT ROWNUM rn, a.* FROM( select * from tb_qna
qna_title LIKE '%' || #{searchContent} || '%'
qna_content LIKE '%' || #{searchContent} || '%'
id LIKE '%' || #{searchContent} || '%'
id is null
ORDER BY created_at DESC) a
ROWNUM <= #{endRow}
) WHERE rn >= #{startRow}

mapper.insert:
insert into tb_qna(qna_title, qna_content, qna_file, created_at,
qna_views, id) values(
#{qna_title}, #{qna_content}, #{qna_file, jdbcType=VARCHAR}, systimestamp, #{qna_views}, #{id}
)

mapper.update:
UPDATE tb_qna
SET qna_title = #{qna_title},
qna_content = #{qna_content},
qna_file = #{qna_file, jdbcType=VARCHAR}
WHERE qna_idx = #{qna_idx}

mapper.select:

SELECT * FROM (
    SELECT ROWNUM rn, a.*
    FROM (
        SELECT * FROM tb_qna 
        WHERE ID = #{id}
        ORDER BY created_at DESC
    ) a
    WHERE ROWNUM <= #{endRow}
)
WHERE rn >= #{startRow}
