mapper.select:

SELECT * FROM (
    SELECT ROWNUM rn, a.*
    FROM (
        SELECT * FROM tb_news ORDER BY created_at DESC
    ) a
    WHERE ROWNUM <= #{endRow}
)
WHERE rn >= #{startRow}

SELECT * FROM ( SELECT ROWNUM rn, a.* FROM( select * from tb_news
news_title LIKE '%' || #{searchContent} || '%'
news_content LIKE '%' || #{searchContent} || '%'
admin_id is null
ORDER BY created_at DESC) a
ROWNUM <= #{endRow}
) WHERE rn >= #{startRow}

mapper.insert:
insert into tb_news(news_title, news_content, news_file, created_at,
updated_at, news_views, admin_id) values(
#{news_title},
#{news_content}, #{news_file, jdbcType=VARCHAR}, systimestamp, systimestamp,
#{news_views}, #{admin_id}
)

mapper.update:
UPDATE tb_news
SET news_title = #{news_title},
news_content = #{news_content},
news_file = #{news_file, jdbcType=VARCHAR}
WHERE news_idx = #{news_idx}
